import { createSLice, configureStore } from '@reduxjs/toolkit';

const initialState = {counter: 0, showCounter: true}

const counterSLice = createSLice({
  name: 'counter',
  initialState,
  reducers: {
    increase(state, actions) {
      state.counter++;
    }
  }
})
